import { useContext, useState } from 'react';
// import { createPortal } from 'react-dom';
import { CompendiumContext } from '../CompendiumContext';
import ModalContent from '../modalcontent';

export default function HyruleList() {
    const [showModal, setShowModal] = useState(false);
    const compendium = useContext(CompendiumContext);
    console.log('compendium', compendium)
    return (
        <ul>
            {compendium.data?.treasure.map((dataObj) => (
                <div className="listItems" key={dataObj.id}> 
                    <li>{dataObj.name}</li>
                    <img 
                        key={dataObj.id}
                        src={dataObj.image}
                        alt="API made an oopsie"
                        onClick={() => setShowModal(true)}
                    /> 
                    {showModal && 
                        <ModalContent onClose={() => setShowModal(false)} item={dataObj} />
                    }
                </div>
            ))}
        </ul>  
    );
};
