export default function CreaturesNFModal ({onClose, item}) {
    console.log(item);



    let drops = "";
    if (item.drops === null) {
        drops += "N/A";
    } else {
        for (let i = 0; i < item.drops.length -2; i++) {
            drops += item.drops[i] + ", "
        }
        if (item.drops.length > 1) {
            let secondToLast = item.drops[item.drops.length - 2];
            drops += " " + secondToLast + " ";
            let last = item.drops[item.drops.length - 1];
            drops += "and " + last;
        } if (item.drops.length === 1) {
            let last = item.drops[item.drops.length - 1];
            drops += last;
        } if (item.drops.length === 0) {
            drops += "None";
        }
    }
    return (
        <ul className="Modal">
            <div>
                <li>{item.name}</li>
                <br />
                <li>{item.description}</li>
                <br />
                <li>Common Locations: {item.common_locations}</li>
                <br />
                <li>Drops: {drops}</li>
                <br />
                <img
                    className="modalImg"
                    key={item.id}
                    src={item.image}
                    alt="API made an oopsie"
                />
            </div>
            <button className="modalButton" onClick={onClose}>Done</button>
        </ul>
    );
}
