import { useContext } from "react";
import { CompendiumContext } from "./CompendiumContext";

export default function ModalContent ({onClose, item}) {
    const compendium = useContext(CompendiumContext);
    console.log("item", item);
    const selectedItem = compendium.data.treasure.find((data) => data.name === item.name);
    if (selectedItem) {
        return (
            <ul>
                <div className="listItems"> 
                    <li>{selectedItem.name}</li>
                    <li>{selectedItem.description}</li>
                    <li>{selectedItem.common_locations}</li>
                    <img
                        key={selectedItem.id} 
                        src={selectedItem.image}
                        alt="API made an oopsie"
                    /> 
                </div>
                <button onClick={onClose}>I'm Good</button>
            </ul>  
        );
    } else {
        return null;
    }
}
